# Backspace String Compare
# https://leetcode.com/problems/backspace-string-compare/
# 

class Solution(object):
    def backspaceCompare(self, S, T):
        """
        :type S: str
        :type T: str
        :rtype: bool
        """

        while S.find("#")>=0  :     
            #pos1=S.index("#")
            if S.index("#")==0:
                S=S[1:]   
            else:
                S=S[:(S.index("#")-1)] + S[(S.index("#")+1):]                
                       

      
        while T.find("#")>=0  :     
            #pos2=T.index("#")
            if T.index("#")==0:
                T=T[1:]   
            else:
                T=T[:(T.index("#")-1)] + T[(T.index("#")+1):]                

        
        return S==T
        

